    public List<List<String>> suggestedProducts(String[] products, String searchWord) {
        List<List<String>> ans = new ArrayList<>();
        List<String> match = new ArrayList<>();
        // 初始化构造match
        for (String product : products) {
            if (product.charAt(0) == searchWord.charAt(0)) {
                match.add(product);
            }
        }
        // 按字典序排序
        Collections.sort(match);
        // 添加至ans
        ans.add(match.subList(0, Math.min(match.size(), 3)));
        // 遍历后续
        for (int i = 1; i < searchWord.length(); ++i) {
            //
            List<String> temp = new ArrayList<>();
            for (String product : match) {
                if (product.length() < i + 1 || product.charAt(i) == searchWord.charAt(i)) {
                    temp.add(product);
                }
            }
            //
            ans.add(temp.subList(0, Math.min(temp.size(), 3)));
            match = temp;
        }
        return ans;
    }
